INSERT INTO jianniang_publish_1 VALUES('6','6','3','1458787917','http://newdev.jianniang.com/www/2016-03-24/1458787932d6.html','《战舰少女R》2016.3.15更新维护公告',NULL,NULL,NULL,NULL,NULL,'亲爱的玩家:<br />\r\n<br />\r\n&nbsp;&nbsp;我们将在2016年3月15日14:00至19:00对安卓服务器进行维护，维护内容为版本更新本次更新范围仅为安卓服务器。<br />\r\n<br />\r\n<br />\r\n本次版本更新主要内容：<br />\r\n<br />\r\n新增功能：<br />\r\n<br />\r\n&nbsp;&nbsp;- 新增关卡：（2-5）深海前哨核心地区 【该章节只开放部分节点】<br />\r\n&nbsp;&nbsp;- 增加新手教程<br />\r\n&nbsp;&nbsp;- 安卓系统增加了远征推送功能<br />\r\n&nbsp;&nbsp;- 增加了好友系统，开放第一期功能<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;1.提督头像<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;2.好友挑战<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;3.提督房间，点击左上角(原提督详细信息按钮)进入<br />\r\n&nbsp;&nbsp;- 以下舰船装扮获得了新的炮弹特效<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;圣诞服&nbsp; &nbsp;&nbsp;&nbsp;内华达<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;圣诞服 &nbsp;&nbsp;&nbsp;&nbsp;欧根亲王<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;红白偶像战 &nbsp;&nbsp;青叶<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;英伦美食&nbsp;&nbsp;&nbsp; 伦敦<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;冬季校服Mk.Ⅱ &nbsp;最上<br />\r\n<br />\r\n&nbsp;&nbsp;- 编队系列任务链增加了新的编队任务<br />\r\n&nbsp;&nbsp;- 位于1-5的苏赫巴托尔大人有了新的能力<br />\r\n<br />\r\n表现增强：<br />\r\n&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;- 将港口更换为春季<br />\r\n&nbsp;&nbsp;- 更换登录界面的背景图<br />\r\n<br />\r\nBUG修复：<br />\r\n&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;- 修复了部分已知BUG<br />\r\n<br />\r\n增加了全新的舰船：<br />\r\n<br />\r\n&nbsp;&nbsp;- 掉落限定<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;战列巡洋舰&nbsp;&nbsp; 金刚<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;战列舰&nbsp;&nbsp;&nbsp;&nbsp; 陆奥<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;航空母舰&nbsp;&nbsp;&nbsp; 飞龙<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;驱逐舰 &nbsp;&nbsp;&nbsp;&nbsp;雪风<br />\r\n<br />\r\n&nbsp;&nbsp;- 掉落/建造<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;轻型航空母舰&nbsp; 飞鹰<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;轻型航空母舰&nbsp; 隼鹰<br />\r\n<br />\r\n新增改造舰船：<br />\r\n<br />\r\n&nbsp;&nbsp;- 以下舰船可以进行改造<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;重巡洋舰&nbsp;&nbsp; 新奥尔良<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;轻巡洋舰&nbsp;&nbsp; 奥马哈<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;驱逐舰&nbsp;&nbsp;&nbsp; 拉菲<br />\r\n<br />\r\n新增部分舰船装扮:<br />\r\n<br />\r\n&nbsp;&nbsp;- 以下舰船获得新的装扮<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;驱逐舰&nbsp;&nbsp;&nbsp; 沙利文<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;驱逐舰&nbsp;&nbsp;&nbsp; 西格斯比<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;潜水艇&nbsp;&nbsp;&nbsp; 大青花鱼<br />\r\n',NULL,NULL,NULL,NULL);
